💡 Tips：PHP 实现滑动块验证码。
实现首先 PHP 要开启 GD 扩展，这是首要的一个条件，如果没有 GD 扩展那么图形函数就无法使用。
源码&lt;?phpnamespace App\Services;class SliderVerifyService&#123;    /**     * 背景     * @var resource     * */    public $background;    /**     * 画布     * @var resource     * */    public $canvas;    /**     * 滑块     * @var resource     * */    public $slide;    /**     * 图片     * @var resource     * */    public $image;    /**     * 背景的宽     * @var int     * */    public int $backgroundWidth = 240;    /**     * 背景的高     * @var int     * */    public int $backgroundHeight = 150;    /**     * 画布的宽     * @var int     * */    public int $slideWidth = 50;    /**     * 画布的高     * */    public int $slideHeight = 50;    /**     * x 坐标     * @var int     * */    public int $_x = 0;    /**     * y 坐标     * @var int     * */    public int $_y = 0;    /**     * 容错象素 越大体验越好，越小破解难度越高     * @var int     * */    public int $_fault = 3;    /**     * 图片质量     * @var int     * */    public int $quality = 80;    /**     * 构建图形     *     * @return void     * */    public function build() : void    &#123;        $this-&gt;init();        $this-&gt;createSlide();        $this-&gt;createBackground();        $this-&gt;merge();    &#125;    /**     * 初始化     *     * @return void     * */    private function init() : void    &#123;        // 设置背景图片，随机的值就是图片的名称        $this-&gt;background = imagecreatefrompng(resource_path() . &#x27;/background/&#x27; . mt_rand(1,7) . &#x27;.png&#x27;);        // 设置画布，并且设置好画布的宽和高        $this-&gt;canvas = imagecreatetruecolor($this-&gt;backgroundWidth, $this-&gt;backgroundHeight);        // 将背景图片拷贝到画布相应位置，宽和高都是用背景统一宽和高        imagecopy(            $this-&gt;canvas, $this-&gt;background,            0, 0, 0, 0,            $this-&gt;backgroundWidth, $this-&gt;backgroundHeight        );        // 在设置一个画布，给滑块使用，宽度采用滑块固定宽度，高度采用背景的高度，这样滑块怎么滑不可能划出背景的高        $this-&gt;slide = imagecreatetruecolor($this-&gt;slideWidth, $this-&gt;backgroundHeight);        // 根据背景的宽减去滑块的宽随机的一个 x 坐标        $this-&gt;_x = mt_rand(50, $this-&gt;backgroundWidth - $this-&gt;slideWidth - 1);        // 根据背景的高减去滑块的高随机一个 y 坐标        $this-&gt;_y = mt_rand(0, $this-&gt;backgroundHeight - $this-&gt;slideHeight - 1);    &#125;    /**     * 创建滑块     *     * @return void     * */    private function createSlide() : void    &#123;        // 设置一个背景图片        $image = imagecreatefrompng(resource_path() . &#x27;/images/mark2.png&#x27;);        // 将滑块拷贝的 init 当中生成的背景图的位置        imagecopy(            $this-&gt;slide, $this-&gt;background,            0, $this-&gt;_y, $this-&gt;_x, $this-&gt;_y,            $this-&gt;slideWidth, $this-&gt;slideHeight        );        // 将滑块拷贝到本次生成的背景图的位置        imagecopy(            $this-&gt;slide, $image,            0, $this-&gt;_y, 0, 0,            $this-&gt;slideWidth, $this-&gt;slideHeight        );        // 设置滑块的透明颜色        imagecolortransparent($this-&gt;slide, 0);        // 销毁上面的生成的那张背景图片        imagedestroy($image);    &#125;    /**     * 创建背景     *     * @return void     * */    private function createBackground() : void    &#123;        // 设置一个背景图片        $image = imagecreatefrompng(resource_path() . &#x27;/images/mark.png&#x27;);        // 设置这个背景图片的透明颜色        imagecolortransparent($image, 0);        // 把 init 当中那个画布拷贝到这次设置的背景图位置        imagecopy(            $this-&gt;canvas, $image,            $this-&gt;_x, $this-&gt;_y, 0, 0,            $this-&gt;slideWidth, $this-&gt;slideHeight        );        // 销毁本次设置的背景图片        imagedestroy($image);    &#125;    /**     * 合并     *     * @return void     * */    private function merge() : void    &#123;        // 在此生成一个画布，最为最终的画布，采用背景的宽与背景高三倍的值        $this-&gt;image = imagecreatetruecolor($this-&gt;backgroundWidth, $this-&gt;backgroundHeight * 3);        // 将本次生成的画布移动到 init 生成的画布的位置        imagecopy(            $this-&gt;image, $this-&gt;canvas,            0, 0, 0, 0,            $this-&gt;backgroundWidth, $this-&gt;backgroundHeight        );        // 将本次生成的画布移动到 init 生成的滑块的位置        imagecopy(            $this-&gt;image, $this-&gt;slide,            0, $this-&gt;backgroundHeight, 0, 0,            $this-&gt;slideWidth, $this-&gt;backgroundWidth        );        // 将本次生成的画布移动到 init 生成的背景的位置        imagecopy(            $this-&gt;image, $this-&gt;background,            0, $this-&gt;backgroundHeight * 2, 0, 0,            $this-&gt;backgroundWidth, $this-&gt;backgroundHeight        );        // 设置本次画布的透明颜色        imagecolortransparent($this-&gt;image, 0);    &#125;    /**     * 获取随机的 x 坐标做标识     *     * @return int     * */    public function getCode() : int    &#123;        return $this-&gt;_x;    &#125;    /**     * 获取图片的base64格式     *     * @return string     * */    public function getImageBase64() : string    &#123;        // 打开输出缓冲        ob_start();        imagewebp($this-&gt;image, null, $this-&gt;quality);        // 返回输出缓冲区的内容        $res = ob_get_contents();        // 输出缓冲区并关闭输出缓冲        ob_end_clean();        return &#x27;data:image/jpeg;base64,&#x27; . base64_encode($res);    &#125;&#125;

